mapper.select:
select itag
from p_tag_menu
where imenu=#{imenu}

SELECT A.imenu imenu,B.menu menu ,COUNT(*) from ( SELECT X.itag,X.imenu,Y.tag FROM p_tag_menu X INNER JOIN p_tag Y ON X.itag=Y.itag ) A INNER JOIN ( SELECT icommon_menu,menu FROM p_common_menu UNION SELECT iuser_menu,menu FROM p_user_menu WHERE iuser=#{iuser} and activation=0 ) B ON A.imenu=B.icommon_menu WHERE A.tag IN
#{item.tag}
GROUP BY A.imenu HAVING COUNT(*)>=#{size}
