mapper.select:
select iuser_menu iuserMenu, activation
from p_user_menu
where menu=#{menu} and iuser=#{iuser}

select iuser_menu iuserMenu, menu,iuser,activation
from p_user_menu
where iuser_menu=#{iuserMenu}

mapper.update:
update p_user_menu
set activation =#{activation}
where iuser_menu=#{iuserMenu}

mapper.select:
select activation
from p_user_menu
where iuser_menu=#{iuserMenu}
